import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { SqlParam } from '../models/SqlParam';

@Injectable({
  providedIn: 'root'
})
export class SqlApiService {

  constructor(private http: HttpClient) { }

  public getFunctionNames() {
    return this.http.get<string[]>('/api/sql/func');
  }

  public getProcedureNames() {
    return this.http.get<string[]>('/api/sql/proc');
  }

  public getParams(name: string) {
    return this.http.get<SqlParam[]>(`/api/sql/params/${name}`);
  }

  public executeProcedure(name: string, params: { [key: string]: any }) {
    var url = `/api/sql/proc/${name}?`;
    for (var key in params) {
      url += `${key}=${params[key]}&`;
    }
    url = url.slice(0, -1);
    url += '/call'
    return this.http.get(url);
  }

  public executeFunction(name: string, params: { [key: string]: any }) {
    var url = `/api/sql/func/${name}?`;
    for (var key in params) {
      url += `${key}=${params[key]}&`;
    }
    url = url.slice(0, -1);
    url += '/call'
    return this.http.get(url);
  }

}
