import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root'
})
export abstract class BaseTableApiService<T> {

  public get url(): string {
    return `/api/${this._url}`
  }

  constructor(protected _url: string, private _http: HttpClient) { }

  getAll() {
    return this._http.get<T[]>(`${this.url}`);
  }

  get(id: number) {
    return this._http.get<T>(`${this.url}/${id}`);
  }

  create(data: T) {
    return this._http.post<T>(`${this.url}`, data);
  }

  update(id: number, data: T) {
    return this._http.put<T>(`${this.url}/${id}`, data);
  }

  delete(id: number) {
    return this._http.delete<T>(`${this.url}/${id}`);
  }
}
