export class TablePattern {
    public name: string;
    public columns: { name: string, type: string }[];
    public api: string;
    public type: any;

    constructor(name: string, api: string, type: any, columns: { name: string, type: string }[]) {
        this.name = name;
        this.api = api;
        this.type = type;
        this.columns = columns;
    }

    public columnsFromType(): { name: string, type: any }[] {
        // include subtypes
        const columns: { name: string, type: any }[] = [];
        for (const key in this.type) {
            if (this.type.hasOwnProperty(key)) {
                const element = this.type[key];
                columns.push({ name: key, type: element });
            }
        }
        return columns;
    }

    static empty() {
        return new TablePattern('', '', null, []);
    }
}
