import { Component, Input } from '@angular/core';
import { ColumnType, TablePattern } from 'src/app/models/TablePattern';

@Component({
  selector: 'app-data-table',
  templateUrl: './data-table.component.html',
  styleUrls: ['./data-table.component.css']
})
export class DataTableComponent {
  @Input() 
  public tablePattern: TablePattern = TablePattern.empty();

  public get columns(): ColumnType[] {
    console.log(this.tablePattern.columns.filter(p => typeof p.type === "string"));
    return this.tablePattern.columns.filter(p => typeof p.type === "string");
  }
}
