import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root'
})
export class BaseTableApiService<T> {

  constructor(protected url: string, private _http: HttpClient) { }

  getAll() {
    return this._http.get<T[]>(`/api/${this.url}`);
  }

  get(id: number) {
    return this._http.get<T>(`/api/${this.url}/${id}`);
  }

  create(data: T) {
    return this._http.post<T>(`/api/${this.url}`, data);
  }

  update(id: number, data: T) {
    return this._http.put<T>(`/api/${this.url}/${id}`, data);
  }

  delete(id: number) {
    return this._http.delete<T>(`/api/${this.url}/${id}`);
  }
}
