import { Component } from '@angular/core';
import { BookModel } from 'src/app/models/BookModels';
import { LibraryModel } from 'src/app/models/LibraryModel';
import { TablePattern } from 'src/app/models/TablePattern';

@Component({
  selector: 'app-tables',
  templateUrl: './tables.component.html',
  styleUrls: ['./tables.component.css']
})
export class TablesComponent {
  private _tablePatterns: TablePattern[] = [
    {
      name: 'Library',
      api: 'library',
      type: LibraryModel,
      columns: [
        { name: 'id', type: 'number' },
        { name: 'name', type: 'string' },
        { name: 'address', type: 'string' },
        { name: 'phone', type: 'string' }
      ]
    }
  ];

  public get tableNames() {
    return this._tablePatterns.map(table => table.name);
  }
}
