export class TablePattern {
    public name: string;
    public columns: { name: string, type: string }[];
    public api: string;
    public type: any;

    constructor(name: string, api: string, type: any, columns: { name: string, type: string }[]) {
        this.name = name;
        this.api = api;
        this.type = type;
        this.columns = columns;
    }

    static columnsFromType(type: any): { name: string, type: any }[] {
        // include subtypes
        const f = (_type: any) => {
            _type = new _type();
            const columns: { name: string, type: any }[] = [];
            for (const key in _type) {
                if (_type.hasOwnProperty(key)) {
                    const element = _type[key];
                    if (typeof element === 'object') {
                        console.log(element);
                        columns.push(...f(element));
                    }
                    else {
                        columns.push({ name: key, type: element });
                    }
                }
            }
            return columns;
        };
        return f(type);
    }

    static empty() {
        return new TablePattern('', '', null, []);
    }
}
